import React, {useState, useEffect} from'react'
import Layout from '../../components/Layout/Layout'
import AdminMenu from '../../components/Layout/AdminMenu'
import toast from "react-hot-toast";
import axios from "axios";
import { Select } from "antd";
import { useNavigate, useParams} from 'react-router-dom';
const { Option } = Select;
const UpdateProduct = () => {
    const navigate = useNavigate();
    const params= useParams();
    const [categories, setCategories] = useState([]);
    const [name, setName] = useState("")
    const [photo, setPhoto] = useState("")
    const [description, setDescription] = useState("")
    const [price, setPrice] = useState("")
    const [category, setCategory] = useState("")
    const [quantity, setQuantity] = useState("")
    const [shipping, setShipping] = useState("");
    const [id , setId] = useState("");
    //get single product 
    const getSingleProduct = async() =>{
        try {
            const {data} = await axios.get(`http://localhost:8080/api/v1/product/get-product/${params.slug}`);
            setName(data.product.name);
            setId(data.product._id);
            setPhoto(data.product.photo);
            setDescription(data.product.description);
            setPrice(data.product.price);
            setQuantity(data.product.quantity);
            setShipping(data.product.shipping);
            setCategory(data.product.category._id);

        } catch (error) {
            console.log(error)
        }
    };

    useEffect(() => {
        getSingleProduct();
        //eslint-disable-next-line
      }, []);

    // Get All Categories
    const getAllCategory = async () => {
      try {
        const { data } = await axios.get(
          "http://localhost:8080/api/v1/category/get-category"
        );
        if (data?.success) {
          setCategories(data?.category);
        }
      } catch (error) {
        console.log(error);
        toast.error("Something went wrong in getting category");
      }
    };
    
    useEffect(() => {
      getAllCategory();
    }, []);
  
    //create product function
    const handleUpdate = async(e) => {
      e.preventDefault();
      try {
        const productData = new FormData()
        productData.append("name", name)
        productData.append("description", description)
        productData.append("price", price)
        productData.append("quantity", quantity)
        photo && productData.append("photo", photo)
        productData.append("category", category)
        productData.append("shipping", shipping);
        if (photo) productData.append("photo", photo);
        
        const {data} = await axios.put(`http://localhost:8080/api/v1/product/update-product/${id}`, productData)
        if(data?.success){
          toast.success('Product Updated successfully')
          navigate('/dashboard/admin/products')
        }
        else{
          toast.error(data?.message)
        }
      } catch (error) {
        console.log(error)
        toast.error('Something went wrong')
      }
    };

    const handleDelete = async() =>{
        try {
            let answer = window.prompt('Are you sure want to delete this product ? ');
            if(!answer) return;
            const {data} = await axios.delete(`http://localhost:8080/api/v1/product/delete-product/${id}`);
            toast.success("Product deleted successfully");
            navigate('/dashboard/admin/products')
        } catch (error) {
            console.log(error)
            toast.error('Something went wrong')
        }
    }
  
  return (
    <Layout title={'Dashboard - Update Product'}>
      <div className='container-fluid mt-3 pt-3'>
      <div className='row'>
             <div className='col-md-3'><AdminMenu/></div>
             <div className='col-md-9'>
               <h1 className='admin-part text-center'>Update Product</h1>
               <div className='m-1 w-75'>
                <Select 
                bordered={false} 
                placeholder='Select a category' 
                size='large' 
                showSearch 
                className='form-select mb-3' 
                onChange={(value)=>{setCategory(value)}}
                value={category}
                >
                 {categories?.map((c) => (
                  <Option key={c._id} value={c._id}>
                    {c.name}
                  </Option>
                 ))}
                </Select>
                <div className='mb-3'>
                   <label className='btn btn-outline-secondary col-md-12'>
                    {photo ? photo.name : 'Upload Photo'}
                   <input 
                   type='file' 
                   id="upload-images"
                   name='photo' 
                   accept='image/*' 
                   onChange={(e) => setPhoto(e.target.files[0]) } 
                   hidden />
                   </label>
                </div>
                <div className='mb-3'>
                  {photo ? (
                    <div className='text-center'>
                      <img 
                      src={URL.createObjectURL(photo)} 
                      alt='product-photo' 
                      height={'200px'} 
                      className='img img-responsive product-img1'/>

                    </div>
                  ) : (
                    <div className='text-center'>
                      <img 
                      src={`http://localhost:8080/api/v1/product/product-photo/${id}`}
                      alt='product-photo' 
                      height={'200px'} 
                      className='img img-responsive product-img1'/>

                    </div>
                  )
                  }
                </div>
                <div className='mb-3'>
                  <input type='text' 
                  value={name} 
                  placeholder='Write a name' 
                  className='form-control'
                  onChange={(e)=> setName(e.target.value)}
                  />
                </div>
                <div className='mb-3'>
                  <textarea 
                  type='text' 
                  value={description} 
                  placeholder='Write a description' 
                  className='form-control'
                  onChange={(e)=> setDescription(e.target.value)}
                  />
                </div>
                <div className='mb-3'>
                  <input type='number' 
                  value={price} 
                  placeholder='Write a price' 
                  className='form-control'
                  onChange={(e)=> setPrice(e.target.value)}
                  />
                </div>
                <div className='mb-3'>
                  <input type='number' 
                  value={quantity} 
                  placeholder='Write a quantity' 
                  className='form-control'
                  onChange={(e)=> setQuantity(e.target.value)}
                  />
                </div>
                <div className='mb-3'>
                  <Select
                  bordered={false}
                  placeholder='Select shipping'
                  size='large'
                  showSearch
                  className='form-select mb-3'
                  onChange={(value)=> setShipping(value)}
                  value={shipping ? 'Yes' : 'No'}
                  >
                    <Option value='0'>No</Option>
                    <Option value='1'>Yes</Option>
                  </Select>
                </div>
                <div className='d-flex m-3 justify-content-between'>
                  <button className='btn btn-warning m-1' onClick={handleUpdate}>UPDATE PRODUCT</button>
                  <button className='btn btn-danger m-1' onClick={handleDelete}>DELETE PRODUCT</button>
                </div>
               </div>
             </div>
          </div>
    </div>
    </Layout>
  )
}

export default UpdateProduct
