import React, {useState, useEffect} from'react'
import Layout from '../../components/Layout/Layout'
import AdminMenu from '../../components/Layout/AdminMenu'
import toast from "react-hot-toast";
import axios from "axios";
import { Select } from "antd";
import { useNavigate } from 'react-router-dom';

const {Option}= Select;

const CreateProduct = () => {
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [name, setName] = useState("")
  const [photo, setPhoto] = useState("")
  const [description, setDescription] = useState("")
  const [price, setPrice] = useState("")
  const [category, setCategory] = useState("")
  const [quantity, setQuantity] = useState("")
  const [shipping, setShipping] = useState("");

  // Get All Categories
  const getAllCategory = async () => {
    try {
      const { data } = await axios.get(
        "http://localhost:8080/api/v1/category/get-category"
      );
      if (data?.success) {
        setCategories(data?.category);
      }
    } catch (error) {
      console.log(error);
      toast.error("Something went wrong in getting category");
    }
  };

  useEffect(() => {
    getAllCategory();
  }, []);

  //create product function
  const handleCreate = async(e) => {
    e.preventDefault();
    try {
      const productData = new FormData()
      productData.append("name", name)
      productData.append("description", description)
      productData.append("price", price)
      productData.append("quantity", quantity)
      productData.append("photo", photo)
      productData.append("category", category)
      productData.append("shipping", shipping);
      if (photo) productData.append("photo", photo);
      
      const {data} = await axios.post('http://localhost:8080/api/v1/product/create-product', productData)
      if(data?.success){
        toast.success('Product created successfully')
        navigate('/dashboard/admin/products')
      }
      else{
        toast.error(data?.message)
      }
    } catch (error) {
      console.log(error)
      toast.error('Something went wrong')
    }
  };


  return (
    <Layout  title={'Dashboard - Create Product'}>
    <div className='container-fluid mt-3 pt-3 detail'>
      <div className='row'>
             <div className='col-md-3'><AdminMenu/></div>
             <div className='col-md-9'>
               <h1 className='admin-part text-center'>Create Product</h1>
               <div className='m-1 w-75'>
                <Select 
                bordered={false} 
                placeholder='Select a category' 
                size='large' 
                showSearch 
                className='form-select mb-3' onChange={(value)=>{setCategory(value)}}>
                 {categories?.map((c) => (
                  <Option key={c._id} value={c._id}>
                    {c.name}
                  </Option>
                 ))}
                </Select>
                <div className='mb-3'>
                   <label className='btn btn-outline-secondary col-md-12'>
                    {photo ? photo.name : 'Upload Photo'}
                   <input 
                   type='file' 
                   id="upload-images"
                   name='photo' 
                   accept='image/*' 
                   onChange={(e) => setPhoto(e.target.files[0]) } 
                   hidden />
                   </label>
                </div>
                <div className='mb-3'>
                  {photo && (
                    <div className='text-center'>
                      <img 
                      src={URL.createObjectURL(photo)} 
                      alt='product-photo' 
                      height={'200px'} 
                      className='img img-responsive'/>

                    </div>
                  )}
                </div>
                <div className='mb-3'>
                  <input type='text' 
                  value={name} 
                  placeholder='Write a name' 
                  className='form-control'
                  onChange={(e)=> setName(e.target.value)}
                  />
                </div>
                <div className='mb-3'>
                  <textarea 
                  type='text' 
                  value={description} 
                  placeholder='Write a description' 
                  className='form-control'
                  onChange={(e)=> setDescription(e.target.value)}
                  />
                </div>
                <div className='mb-3'>
                  <input type='number' 
                  value={price} 
                  placeholder='Write a price' 
                  className='form-control'
                  onChange={(e)=> setPrice(e.target.value)}
                  />
                </div>
                <div className='mb-3'>
                  <input type='number' 
                  value={quantity} 
                  placeholder='Write a quantity' 
                  className='form-control'
                  onChange={(e)=> setQuantity(e.target.value)}
                  />
                </div>
                <div className='mb-3'>
                  <Select
                  bordered={false}
                  placeholder='Select shipping'
                  size='large'
                  showSearch
                  className='form-select mb-3'
                  onChange={(value)=> setShipping(value)}
                  >
                    <Option value='0'>No</Option>
                    <Option value='1'>Yes</Option>
                  </Select>
                </div>
                <div className='mb-3'>
                  <button className='btn btn-success' onClick={handleCreate}>CREATE PRODUCT</button>
                </div>
               </div>
             </div>
          </div>
    </div>
    </Layout>
  )
}

export default CreateProduct
